svg.script:

var texts = [
  "Caio Foti - GitHub Profile",
  "Hey there, I'm Caio, welcome to my GitHub profile!",
  "Loading..."
];
var currentTextIndex = 0;
var currentCharIndex = 0;

function typeText() {
  if (currentTextIndex < texts.length) {
    var textElement = document.getElementById("text" + (currentTextIndex + 1));
    textElement.textContent = texts[currentTextIndex].substring(0, currentCharIndex);
    currentCharIndex++;
    if (currentCharIndex <= texts[currentTextIndex].length) {
      setTimeout(typeText, 100); // Typing speed
    } else {
      currentTextIndex++;
      currentCharIndex = 0;
      setTimeout(typeText, 2000); // Delay between texts
    }
  }
}

typeText();
